// Genera el timestamp en formato texto (STRING)
var timestamp = new Date().toLocaleString();
msg.timestamp = timestamp;
return msg;

// Nombre de la columna que quieres analizar
const columna = msg.columna;

// Inicializar el valor máximo
let max = -Infinity;

// Buscar el valor máximo en la columna especificada
max = msg.payload.reduce((acumulador, row) => {
    const valor = parseFloat(row[columna]);
    return (!isNaN(valor) && valor > acumulador) ? valor : acumulador;
}, -Infinity);

msg.payload = max;
return msg;

let potencia = [];
// Array de valores de potencia
for (let index = 0; index < msg.payload.length; index++) {
    potencia.push(msg.payload[index]["potencia"]);    
}
// Función para calcular el valor máximo
const valorMaximo = Math.max(...potencia);
msg.potencia = valorMaximo;
msg.promedio = valorMaximo/msg.payload.length;
return msg;

